import React, { useEffect, useState } from "react";
import Axios from "axios";
function Scouting() {
  const [matchNumber, setMatchNumber] = useState();
  const [teamNumber, setTeamNumber] = useState();
  const [teamName, setTeamName] = useState();
  const [autoBallsShot, setAutoBallsShot] = useState();
  const [autoBallsSuccess, setAutoBallsSuccess] = useState();
  const [teleopBallsShot, setTeleopBallsShot] = useState();
  const [teleopBallsSucces, setTeleopBallsSucess] = useState();
  const [wasDefenseBot, setWasDefenseBot] = useState();
  const [climbHeight, setClimbHeight] = useState();
  const [timeToClimb, setTimeToClimb] = useState();
  const [wentToEnemySide, setWentToEnemySide] = useState();
  const [notes, setNotes] = useState();

  const [teamChosen, setTeamChosen] = useState();
  const [namePlaceholder, setNamePlaceholder] = useState();
  const basicInfo = [
    { title: "Team Name", use: setTeamName, placeholder: namePlaceholder },
  ];

  const auto = [
    { title: "Balls Shot", use: setAutoBallsShot },
    { title: "Success", use: setAutoBallsSuccess },
  ];

  const teleop = [
    { title: "Balls Shot", use: setTeleopBallsShot },
    { title: "Success", use: setTeleopBallsSucess },
  ];
  const other = [{ title: "Climb Time (s)", use: setTimeToClimb }];
  const enemySide_select = ["Yes, successfully", "Yes, unsuccessfully", "No"];
  const climbLevel_select = [
    "None",
    "Low Bar",
    "Mid Bar",
    "High Bar",
    "Traversal Bar",
  ];

  const defense_select = ["Yes", "No"];

  const submitMatch = () => {
    Axios.post("http://192.168.68.164:3002/putTeamMatch", {
      match_number: matchNumber,
      team_name: teamName,
      team_number: teamNumber,
      auto_Balls_shot: autoBallsShot,
      auto_Balls_success: autoBallsSuccess,
      teleop_Balls_shot: teleopBallsShot,
      teleop_Balls_success: teleopBallsSucces,
      was_Defense: wasDefenseBot,
      climb_height: climbHeight,
      climb_time: timeToClimb,
      went_To_Enemy: wentToEnemySide,
      notes: notes,
    }).then(() => {});
  };

  Axios.get(`http://192.168.68.164:3002/getNameFromID/${teamNumber}`).then(
    (res) => {
      setNamePlaceholder(res);
    }
  );
  const [matchList, setMatchList] = useState();
  Axios.get(`http://192.168.68.164:3002/getMatchList`).then((res) =>
    setMatchList(res)
  );
  Axios.get();
  return (
    <>
      <br />
      <div
        className="container d-flex"
        style={{
          backgroundColor: "rgb(245, 245, 245)",
          marginTop: "-42px",
          paddingTop: "20px",
          paddingBottom: "20px",
        }}
      >
        <div className="row">
          {" "}
          <div className="col-1">
            <i
              class="fa-regular fa-plus"
              // onClick={() => addTeamController(teamChosen)}
            ></i>
          </div>
          <div className="col-6">
            <small
              className=""
              // onClick={() => addTeamController(teamChosen)}
            >
              Add Statistics for{" "}
            </small>
          </div>
          <div className="col-2">
            <small>
              <select
                name=""
                id=""
                onChange={(e) => setTeamChosen(e.target.value)}
                style={{ marginLeft: "-50px" }}
              >
                <option value="0"> Pick Team</option>
              </select>
            </small>
          </div>
          <div className="row">
            <p style={{ fontSize: "10px" }}>
              {" "}
              Remember to input all team numbers, match type, and match number
              (won't work without it).
            </p>
          </div>
        </div>
      </div>{" "}
      <div className="container teamStats" style={{ display: "none" }}>
        <br />
        <u>
          <small style={{ fontSize: "13px" }}>Basic Info:</small>
        </u>

        <div className="row">
          <div className="col-4" style={{ fontSize: "15px" }}>
            {" "}
            Team Number:
          </div>
          <div className="col-4">{teamNumber}</div>
        </div>
        <br />
        {basicInfo.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      placeholder={val.placeholder}
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}
        <u>
          <small style={{ fontSize: "13px" }}>Autonomous:</small>{" "}
        </u>
        {auto.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}
        <u>
          <small style={{ fontSize: "13px" }}>Teleop:</small>{" "}
        </u>
        {teleop.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}

        <u>
          <small style={{ fontSize: "13px" }}>Climb & Other</small>
        </u>
        <div className="row">
          <div className="col-4">Climb Level</div>
          <div className="col-4">
            <select
              name=""
              id=""
              style={{ width: "215px" }}
              onChange={(e) => setClimbHeight(e.target.value)}
            >
              <option value="0"> Pick Value</option>
              {climbLevel_select.map((value, key) => {
                return (
                  <option value={key} id={key}>
                    {value}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <br />
        {other.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}
        <div className="row">
          <div className="col-4">Defense Bot?</div>
          <div className="col-4">
            {" "}
            <select
              name=""
              id=""
              style={{ width: "215px" }}
              onChange={(e) => setWasDefenseBot(e.target.value)}
            >
              <option value="0"> Pick Value</option>
              {defense_select.map((value, key) => {
                return (
                  <option value={key} id={key}>
                    {value}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <br />
        <div className="row">
          <div className="col-4">Enemy Side</div>
          <div className="col-4">
            {" "}
            <select
              name=""
              id=""
              style={{ width: "215px" }}
              onChange={(e) => setWentToEnemySide(e.target.value)}
            >
              <option value="0"> Pick Value</option>
              {enemySide_select.map((value, key) => {
                return (
                  <option value={key} id={key}>
                    {value}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <br />

        <div class="form-group">
          <label for="exampleFormControlTextarea1">
            Additional Comments (if needed)
          </label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            onChange={(e) => setNotes(e.target.value)}
          ></textarea>
        </div>
        <br />
        <button
          className="submit btn btn-primary"
          type="button"
          style={{ marginLeft: "20px" }}
          onClick={submitMatch}
        >
          Add Team {teamNumber}
        </button>
        <br />
        <small>
          From
          {/* {matchType} */}
          {matchNumber}
        </small>
        <br />
        <br />
      </div>
    </>
  );
}
export default Scouting;
