import React, { useEffect, useState } from "react";
import Axios from "axios";
function Scouting() {
  const [matchNumber, setMatchNumber] = useState();
  const [teamNumber, setTeamNumber] = useState();
  const [teamName, setTeamName] = useState();
  const [autoBallsShot, setAutoBallsShot] = useState();
  const [autoBallsSuccess, setAutoBallsSuccess] = useState();
  const [teleopBallsShot, setTeleopBallsShot] = useState();
  const [teleopBallsSucces, setTeleopBallsSucess] = useState();
  const [wasDefenseBot, setWasDefenseBot] = useState();
  const [climbHeight, setClimbHeight] = useState();
  const [timeToClimb, setTimeToClimb] = useState();
  const [wentToEnemySide, setWentToEnemySide] = useState();
  const [notes, setNotes] = useState();

  const [blue1, setBlue1] = useState();
  const [blue2, setBlue2] = useState();
  const [blue3, setBlue3] = useState();
  const [red1, setRed1] = useState();
  const [red2, setRed2] = useState();
  const [red3, setRed3] = useState();

  const [matchType, setMatchType] = useState();
  const [teamChosen, setTeamChosen] = useState();
  const [namePlaceholder, setNamePlaceholder] = useState();
  const basicInfo = [
    { title: "Team Name", use: setTeamName, placeholder: namePlaceholder },
  ];

  const auto = [
    { title: "Balls Shot", use: setAutoBallsShot },
    { title: "Success", use: setAutoBallsSuccess },
  ];

  const teleop = [
    { title: "Balls Shot", use: setTeleopBallsShot },
    { title: "Success", use: setTeleopBallsSucess },
  ];
  const other = [{ title: "Climb Time (s)", use: setTimeToClimb }];
  const enemySide_select = ["Yes, successfully", "Yes, unsuccessfully", "No"];
  const climbLevel_select = [
    "None",
    "Low Bar",
    "Mid Bar",
    "High Bar",
    "Traversal Bar",
  ];

  const matchTeamsBlue_input = [
    { name: "Team 1", use: setBlue1 },
    { name: "Team 2", use: setBlue2 },
    { name: "Team 3", use: setBlue3 },
  ];
  const matchTeamsRed_input = [
    { name: "Team 1", use: setRed1 },
    { name: "Team 2", use: setRed2 },
    { name: "Team 3", use: setRed3 },
  ];
  const matchType_select = [
    { name: "Qualifier" },
    { name: "Playoff" },
    { name: "Practice" },
  ];

  const addStatsForTeam_select = [
    { title: "Blue 1", value: blue1 },
    { title: "Blue 2", value: blue2 },
    { title: "Blue 3", value: blue3 },
    { title: "Red 1", value: red1 },
    { title: "Red 2", value: red2 },
    { title: "Red 3", value: red3 },
  ];

  const addTeamController = (props) => {
    if (props === "0") {
      setTeamNumber(blue1);
    } else if (props === "1") {
      setTeamNumber(blue2);
    } else if (props === "2") {
      setTeamNumber(blue3);
    } else if (props === "3") {
      setTeamNumber(red1);
    } else if (props === "4") {
      setTeamNumber(red2);
    } else if (props === "5") {
      setTeamNumber(red3);
    }
    const stats = document.getElementsByClassName("teamStats")[0];
    if (
      (props === "0" ||
        props === "1" ||
        props === "2" ||
        props === "3" ||
        props === "4" ||
        props === "5") &&
      matchNumber >= 0
    ) {
      stats.style.display = "block";
    }
    const { data: post } = Axios.get(
      `http://localhost:3002/getNameFromID/${teamNumber}`
    );
    namePlaceholder(post);

    console.log(namePlaceholder);
  };
  const defense_select = ["Yes", "No"];

  const submitMatch = () => {
    Axios.post("http://localhost:3002/putTeamMatch", {
      match_number: matchNumber,
      team_name: teamName,
      team_number: teamNumber,
      auto_Balls_shot: autoBallsShot,
      auto_Balls_success: autoBallsSuccess,
      teleop_Balls_shot: teleopBallsShot,
      teleop_Balls_success: teleopBallsSucces,
      was_Defense: wasDefenseBot,
      climb_height: climbHeight,
      climb_time: timeToClimb,
      went_To_Enemy: wentToEnemySide,
      notes: notes,
    }).then(() => {});
    Axios.post("http://localhost:3002/putMatch", {
      match_type: matchType,
      match_number: matchNumber,
      blue1: blue1,
      blue2: blue2,
      blue3: blue3,
      red1: red1,
      red2: red2,
      red3: red3,
    }).then(() => {});
  };

  return (
    <>
      <div className="container">
        <br />
        <h3>Register Match</h3>
        <div className="row">
          <div className="col-2"></div>
          <div className="col-5">
            <select
              name=""
              id=""
              style={{ width: "125px" }}
              onChange={(e) => setMatchType(e.target.value)}
            >
              <option value="0"> Pick Type</option>
              {matchType_select.map((value, key) => {
                return <option value={key}>{value.name}</option>;
              })}
            </select>
          </div>
          <div className="col-2">
            <div className="input-group">
              <input
                type="number"
                className="input-control rounded"
                onChange={(e) => setMatchNumber(e.target.value)}
                style={{ width: "100px" }}
                placeholder="Number"
              />
            </div>
          </div>
        </div>
        <br />
        <div className="row">
          <small>
            OR Use Pre-registered Game:
            <select
              name=""
              id=""
              onChange={(e) => setMatchNumber(e.target.value)}
            >
              <option value="0">Select Game</option>
              {/* {matchList.map((value, key) => {
                const [gameType, setGameType] = useState();
                if (value.gameType == 0) {
                  setGameType("Qualifier");
                } else if (value.gameType == 1) {
                  setGameType("Playoff");
                } else if (value.gameType == 2) {
                  setGameType("Practice");
                }
                return (
                  <option value={key}>
                    {gameType}: {value.gameNumber}
                  </option>
                );
              })} */}
            </select>
          </small>
        </div>
        <br />
        <div className="row d-flex justify-content-center">
          <div className="col-2">Blue</div>
          {matchTeamsBlue_input.map((value, key) => {
            return (
              <div className="col-3">
                <div className="input-group">
                  <input
                    type="number"
                    className="input-control rounded"
                    placeholder={value.name}
                    style={{ width: "80px" }}
                    onChange={(e) => value.use(e.target.value)}
                  />
                </div>
              </div>
            );
          })}
        </div>
        <div className="row mt-2 d-flex justify-content-center">
          <div className="col-2">Red</div>
          {matchTeamsRed_input.map((value, key) => {
            return (
              <div className="col-3">
                <div className="input-group">
                  <input
                    type="number"
                    className="input-control rounded"
                    placeholder={value.name}
                    style={{ width: "80px" }}
                    onChange={(e) => value.use(e.target.value)}
                  />
                </div>
              </div>
            );
          })}
        </div>
      </div>
      <br />
      <div className="container  d-flex">
        <div className="row">
          {" "}
          <div className="col-1">
            <i
              class="fa-regular fa-plus"
              onClick={() => addTeamController(teamChosen)}
            ></i>
          </div>
          <div className="col-6">
            <small className="" onClick={() => addTeamController(teamChosen)}>
              Add Statistics for{" "}
            </small>
          </div>
          <div className="col-2">
            <small>
              <select
                name=""
                id=""
                onChange={(e) => setTeamChosen(e.target.value)}
                style={{ marginLeft: "-50px" }}
              >
                <option value="0"> Pick Team</option>

                {addStatsForTeam_select.map((value, key) => {
                  return (
                    <option value={key}>
                      {value.title}: {value.value}
                    </option>
                  );
                })}
              </select>
            </small>
          </div>
          <div className="row">
            <p style={{ fontSize: "10px" }}>
              {" "}
              Remember to input all team numbers, match type, and match number
              (won't work without it).
            </p>
          </div>
        </div>
      </div>{" "}
      <div className="container teamStats" style={{ display: "none" }}>
        <br />
        <u>
          <small style={{ fontSize: "13px" }}>Basic Info:</small>
        </u>

        <div className="row">
          <div className="col-4" style={{ fontSize: "15px" }}>
            {" "}
            Team Number:
          </div>
          <div className="col-4">{teamNumber}</div>
        </div>
        <br />
        {basicInfo.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      placeholder={val.placeholder}
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}
        <u>
          <small style={{ fontSize: "13px" }}>Autonomous:</small>{" "}
        </u>
        {auto.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}
        <u>
          <small style={{ fontSize: "13px" }}>Teleop:</small>{" "}
        </u>
        {teleop.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}

        <u>
          <small style={{ fontSize: "13px" }}>Climb & Other</small>
        </u>
        <div className="row">
          <div className="col-4">Climb Level</div>
          <div className="col-4">
            <select
              name=""
              id=""
              style={{ width: "215px" }}
              onChange={(e) => setClimbHeight(e.target.value)}
            >
              <option value="0"> Pick Value</option>
              {climbLevel_select.map((value, key) => {
                return (
                  <option value={key} id={key}>
                    {value}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <br />
        {other.map((val, key) => {
          return (
            <>
              <div className="row">
                <div className="col-4" style={{ fontSize: "15px" }}>
                  {val.title}
                </div>
                <div className="col-4">
                  <div className="input-group">
                    <input
                      type="text"
                      className="input-control align-middle"
                      onChange={(e) => {
                        val.use(e.target.value);
                      }}
                      style={{
                        height: "30px",
                        marginTop: "-5px",
                      }}
                    />
                  </div>
                </div>
              </div>
              <br />
            </>
          );
        })}
        <div className="row">
          <div className="col-4">Defense Bot?</div>
          <div className="col-4">
            {" "}
            <select
              name=""
              id=""
              style={{ width: "215px" }}
              onChange={(e) => setWasDefenseBot(e.target.value)}
            >
              <option value="0"> Pick Value</option>
              {defense_select.map((value, key) => {
                return (
                  <option value={key} id={key}>
                    {value}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <br />
        <div className="row">
          <div className="col-4">Enemy Side</div>
          <div className="col-4">
            {" "}
            <select
              name=""
              id=""
              style={{ width: "215px" }}
              onChange={(e) => setWentToEnemySide(e.target.value)}
            >
              <option value="0"> Pick Value</option>
              {enemySide_select.map((value, key) => {
                return (
                  <option value={key} id={key}>
                    {value}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <br />

        <div class="form-group">
          <label for="exampleFormControlTextarea1">
            Additional Comments (if needed)
          </label>
          <textarea
            class="form-control"
            id="exampleFormControlTextarea1"
            rows="3"
            onChange={(e) => setNotes(e.target.value)}
          ></textarea>
        </div>
        <br />
        <button
          className="submit btn btn-primary"
          type="button"
          style={{ marginLeft: "20px" }}
          onClick={submitMatch}
        >
          Add Team {teamNumber}
        </button>
        <br />
        <small>
          From {matchType} {matchNumber}
        </small>
        <br />
        <br />
      </div>
    </>
  );
}
export default Scouting;
