import React, { useEffect } from "react";

function MatchTypeSelector(props) {
  useEffect(() => {
    const showSMTH = () => {
      const register = document.getElementsByClassName("register")[0];
      if (props.active === "register") {
        register.style.background = "rgb(27, 27, 27)";
      }
    };
    showSMTH();
  });
  return (
    <div>
      <br />
      <div className="row" style={{ textDecoration: "underline" }}>
        <div className="col-1"></div>
        <div className="col-4">
          <h6 className="register" style={{ paddingBottom: "20px" }}>
            Register Match
          </h6>
        </div>
        <div className="col-2"></div>
        <div className="col-4">
          <h6>Match Stats</h6>
        </div>
        <div className="col-1"></div>
      </div>
    </div>
  );
}

export default MatchTypeSelector;
